// Sort the features by the 'id' property
roadData.features.sort((a, b) => {
    const nameA = a.properties.id.toUpperCase(); // Ignore case
    const nameB = b.properties.id.toUpperCase(); // Ignore case
    if (nameA < nameB) return -1;
    if (nameA > nameB) return 1;
    return 0;
});

// Generate alphabet buttons
function createAlphabetButtons() {
    const alphabet = 'ABCDEFGHIJKLMNOPRSTUVWYZ'.split('');
    const alphabetButtonsContainer = document.getElementById('alphabetButtons');

    alphabet.forEach(letter => {
        const button = document.createElement('button');
        button.textContent = letter;
        button.onclick = () => displayRoadsByLetter(letter, button);
        alphabetButtonsContainer.appendChild(button);
    });
}

// Display roads starting with a specific letter
function displayRoadsByLetter(letter, button) {
    const roadList = document.getElementById('roadNamesList');
    roadList.innerHTML = ''; // Clear previous list

    // Remove 'active' class from all buttons
    document.querySelectorAll('button.active').forEach(btn => btn.classList.remove('active'));
    button.classList.add('active')

    roadData.features.forEach(feature => {
        const roadName = feature.properties.id;
        if (roadName.startsWith(letter)) {
            const listItem = document.createElement('li');
            listItem.textContent = roadName;
            listItem.onclick = () => highlightRoad(roadName);
            roadList.appendChild(listItem);
        }
    });
}

// Load all roads initially
function loadAllRoads() {
    const roadList = document.getElementById('roadNamesList');
    roadList.innerHTML = ''; // Clear previous list

    roadData.features.forEach(feature => {
        const roadName = feature.properties.id;
        const listItem = document.createElement('li');
        listItem.textContent = roadName;
        listItem.onclick = () => highlightRoad(roadName);
        roadList.appendChild(listItem);
    });
}

// Highlight road function
function highlightRoad(roadName) {
    const layer = roadLayers[roadName];
    if (!layer) return;

    // Zoom to the road
    mymap.fitBounds(layer.getBounds());

    // Change style to highlight
    layer.setStyle({ color: "red", weight: 6, opacity: 0 });

    // Reset style after 5 seconds
    setTimeout(() => {
        layer.setStyle({ color: "gray", weight: 4, opacity: 0 });
    }, 3000);
}

// Initial setup
createAlphabetButtons();
loadAllRoads();


const center_x = 117.3;
const center_y = 172.8;
const scale_x = 0.02072;
const scale_y = 0.0205;

CUSTOM_CRS = L.extend({}, L.CRS.Simple, {
    projection: L.Projection.LonLat,
    scale: function (zoom) {
        return Math.pow(2, zoom);
    },
    zoom: function (sc) {
        return Math.log(sc) / 0.6931471805599453;
    },
    distance: function (pos1, pos2) {
        var x_difference = pos2.lng - pos1.lng;
        var y_difference = pos2.lat - pos1.lat;
        return Math.sqrt(x_difference * x_difference + y_difference * y_difference);
    },
    transformation: new L.Transformation(scale_x, center_x, -scale_y, center_y),
    infinite: true
});

var SateliteStyle = L.tileLayer('mapStyles/styleSatelite/{z}/{x}/{y}.jpg', { minZoom: 0, maxZoom: 8, noWrap: true, continuousWorld: false, attribution: 'Online map GTA V', id: 'SateliteStyle map', }),
    AtlasStyle = L.tileLayer('mapStyles/styleAtlas/{z}/{x}/{y}.jpg', { minZoom: 0, maxZoom: 5, noWrap: true, continuousWorld: false, attribution: 'Online map GTA V', id: 'styleAtlas map', });

var mymap = L.map('map', {
    crs: CUSTOM_CRS,
    minZoom: 1,
    maxZoom: 5,
    zoom: 3,
    maxNativeZoom: 5,
    preferCanvas: true,
    layers: [AtlasStyle],
    center: [0, 0]
});

let roadsLayer = L.geoJSON(roadData, {
    style: function (feature) {
        return { color: "red", weight: 4, opacity: Number(MAPPING_MODE) };
    }
}).addTo(mymap);

// Initialize FeatureGroup to store drawn items
var drawnItems = new L.FeatureGroup();
mymap.addLayer(drawnItems);

// Set up Leaflet Draw options for line tool (polyline)
var drawControl = new L.Control.Draw({
    draw: {
        polyline: {
            shapeOptions: {
                color: 'red',
                weight: 3
            }
        },
        polygon: false,  // Disable polygon tool
        rectangle: false, // Disable rectangle tool
        circle: false,    // Disable circle tool
        marker: false     // Disable marker tool
    },
    edit: {
        featureGroup: drawnItems
    }
});
if (MAPPING_MODE) {
    mymap.addControl(drawControl);
}

// Function to copy text to clipboard
function copyToClipboard(text) {
    navigator.clipboard.writeText(text + ',');
}

// Event listener for when a new line (road) is created
mymap.on(L.Draw.Event.CREATED, function (event) {
    var layer = event.layer;
    drawnItems.addLayer(layer); // Add the new road to the FeatureGroup

    // Prompt the user to enter a name for the road
    var roadName = window.prompt("Enter a name for this road:");
    if (!roadName) {
        alert("Road name is required.");
        return;
    }

    // Convert the drawn layer to GeoJSON format
    var newRoadGeoJSON = layer.toGeoJSON();
    newRoadGeoJSON.properties.id = roadName; // Add the road name to the properties

    // Add the new road to roadData
    roadData.features.push(newRoadGeoJSON);

    // Add the new road to the map layer with a unique style
    L.geoJSON(newRoadGeoJSON, {
        style: { color: "red", weight: 4, opacity: Number(MAPPING_MODE) }
    }).addTo(mymap);

    // Store the new road layer in roadLayers for easy access
    roadLayers[roadName] = layer;

    // Add the road to the list dynamically
    const listItem = document.createElement("li");
    listItem.textContent = roadName;
    listItem.style.cursor = "pointer";
    listItem.onclick = function () {
        highlightRoad(roadName);
    };
    document.getElementById("roadNamesList").appendChild(listItem);

    // Copy the new GeoJSON feature to the clipboard
    copyToClipboard(JSON.stringify(newRoadGeoJSON));
});

var layersControl = L.control.layers({ "Satelite": SateliteStyle, "Atlas": AtlasStyle }).addTo(mymap);

function customIcon(icon) {
    return L.icon({
        iconUrl: `blips/${icon}.png`,
        iconSize: [20, 20],
        iconAnchor: [20, 20],
        popupAnchor: [-10, -27]
    });
}

// Load GeoJSON roads and store each layer in a dictionary by road name for easy access
let roadLayers = {};

// Function to add roads to the map and populate the list
function addRoads(roadData) {
    L.geoJSON(roadData, {
        style: function (feature) {
            return { color: "red", weight: 4, opacity: Number(MAPPING_MODE) };
        },
        onEachFeature: function (feature, layer) {
            const roadName = feature.properties.id;
            roadLayers[roadName] = layer; // Store each road layer by name

            // Create a list item for each road
            const listItem = document.createElement("li");
            listItem.textContent = roadName;
            listItem.style.cursor = "pointer";
            listItem.onclick = function () {
                highlightRoad(roadName);
            };
            document.getElementById("roadNamesList").appendChild(listItem);
        }
    }).addTo(mymap);
}

let highlightedRoadLayer;

// Function to highlight a specific road by name for 5 seconds
function highlightRoad(roadName) {
    const layer = roadLayers[roadName];
    if (!layer) return;

    // Remove old highlighting
    if (highlightedRoadLayer) {
        highlightedRoadLayer.setStyle({ color: "gray", weight: 4, opacity: 0 });
    }
    highlightedRoadLayer = layer;

    // Zoom to the road
    mymap.fitBounds(layer.getBounds());

    // Change style to highlight
    layer.setStyle({ color: "red", weight: 6, opacity: 1 });
}

